CREATE OR REPLACE PROCEDURE         FOCUSPP.FCS_BATCH_LOG (P_STATUS IN VARCHAR2, P_BATCH_NM IN VARCHAR2)
IS
V_EXISTS NUMBER;
BEGIN    
    SELECT COUNT(1) INTO V_EXISTS FROM BATCH_LOG WHERE BATCH_CODE = P_BATCH_NM;
    IF V_EXISTS > 0 THEN
        UPDATE BATCH_LOG SET STATUS = P_STATUS, EXEC_DATE = SYSDATE WHERE BATCH_CODE = P_BATCH_NM;
    ELSE 
        INSERT INTO BATCH_LOG (BATCH_CODE, STATUS, EXEC_DATE) VALUES (P_BATCH_NM, P_STATUS, SYSDATE);
    END IF;
    COMMIT;
END;
