CREATE OR REPLACE PROCEDURE         FOCUSPP.FCS_USER_UPLOAD_LOG (P_STATUS IN VARCHAR2, P_UT_CODE IN VARCHAR2)
IS
V_EXISTS NUMBER;
BEGIN    
    SELECT COUNT(1) INTO V_EXISTS FROM USER_UPLOAD_LOG WHERE UT_CODE = P_UT_CODE;
    IF V_EXISTS > 0 THEN
        UPDATE USER_UPLOAD_LOG SET UT_STATUS = P_STATUS, UT_EXEC_DATE = SYSDATE WHERE UT_CODE = P_UT_CODE;
    ELSE 
        INSERT INTO USER_UPLOAD_LOG (USER_UPLOAD_LOG_ID, UT_CODE, UT_STATUS, UT_EXEC_DATE) VALUES (USER_UPLOAD_LOG_SEQ.NEXTVAL, P_UT_CODE, P_STATUS, SYSDATE);
    END IF;
    COMMIT;
END;
