CREATE OR REPLACE PROCEDURE         FOCUSPP.FCS_USER_UPLOAD_SYNC_LOG (P_STATUS IN VARCHAR2, P_UT_CODE IN VARCHAR2)
IS
V_EXISTS NUMBER;
BEGIN    
    SELECT COUNT(1) INTO V_EXISTS FROM USER_UPLOAD_LOG WHERE SYNC_CODE = P_UT_CODE;
    IF V_EXISTS > 0 THEN
        UPDATE USER_UPLOAD_LOG SET UT_CODE = 'UTSYNC', SYNC_STATUS = P_STATUS, SYNC_EXEC_DATE = SYSDATE WHERE SYNC_CODE = P_UT_CODE;
    ELSE 
        INSERT INTO USER_UPLOAD_LOG (USER_UPLOAD_LOG_ID, UT_CODE, SYNC_CODE, SYNC_STATUS, SYNC_EXEC_DATE) VALUES (USER_UPLOAD_LOG_SEQ.NEXTVAL, 'UTSYNC',P_UT_CODE, P_STATUS, SYSDATE);
    END IF;
    COMMIT;
END;
